import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const Header = ({ user, onLogout }) => {
  return (
    <header className="header">
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        {/* Logo */}
        <Link to="/" className="text-3xl font-bold text-primary hover:text-primary-dark transition-colors">
          üõçÔ∏è Bobo Store
        </Link>

        {/* Navigation */}
        <nav className="hidden md:flex space-x-8">
          <Link to="/" className="text-gray-700 hover:text-primary transition-colors font-semibold">
            Home
          </Link>
          <Link to="/products" className="text-gray-700 hover:text-primary transition-colors font-semibold">
            Products
          </Link>
          <Link to="/cart" className="text-gray-700 hover:text-primary transition-colors font-semibold">
            Cart
          </Link>
        </nav>

        {/* User Auth Section */}
        <div className="flex items-center space-x-4">
          {user ? (
            <motion.div 
              className="flex items-center space-x-4"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
            >
              <span className="text-gray-700">Welcome, {user.name}</span>
              <motion.button 
                onClick={onLogout}
                className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition-colors"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                Logout
              </motion.button>
            </motion.div>
          ) : (
            <motion.div 
              className="flex space-x-4"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
            >
              <Link to="/login">
                <motion.button 
                  className="bg-primary text-white px-6 py-2 rounded-lg hover:bg-primary-dark transition-colors"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  Login
                </motion.button>
              </Link>
              <Link to="/register">
                <motion.button 
                  className="border border-primary text-primary px-6 py-2 rounded-lg hover:bg-primary hover:text-white transition-colors"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  Sign Up
                </motion.button>
              </Link>
            </motion.div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
