import { AnimatePresence } from 'framer-motion';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import HomePage from './pages/HomePage';        // Changed from Home to HomePage
import ProductPage from './pages/ProductPage';  // Changed from Products to ProductPage
import CartPage from './pages/CartPage';        // Changed from Cart to CartPage
import LoginPage from './pages/LoginPage';      // Changed from Login to LoginPage
import RegisterPage from './pages/RegisterPage';// Changed from Register to RegisterPage
import PageTransition from './components/PageTransition';

function AnimatedRoutes() {
  const location = useLocation();

  return (
    <AnimatePresence mode="wait">
      <Routes location={location} key={location.pathname}>
        <Route path="/" element={
          <PageTransition>
            <HomePage />      {/* Changed from Home to HomePage */}
          </PageTransition>
        } />
        <Route path="/products" element={
          <PageTransition>
            <ProductPage />   {/* Changed from Products to ProductPage */}
          </PageTransition>
        } />
        <Route path="/cart" element={
          <PageTransition>
            <CartPage />      {/* Changed from Cart to CartPage */}
          </PageTransition>
        } />
        <Route path="/login" element={
          <PageTransition>
            <LoginPage />     {/* Changed from Login to LoginPage */}
          </PageTransition>
        } />
        <Route path="/register" element={
          <PageTransition>
            <RegisterPage />  {/* Changed from Register to RegisterPage */}
          </PageTransition>
        } />
      </Routes>
    </AnimatePresence>
  );
}

function App() {
  return (
    <Router>
      <div className="App">
        <Header />
        <main className="min-h-screen">
          <AnimatedRoutes />
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
