import { useState } from 'react';
import { motion } from 'framer-motion';

const ProductPage = () => {
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [sortBy, setSortBy] = useState('name');

  const products = [
    { id: 1, name: 'Premium Headphones', price: 199.99, category: 'electronics', image: '/api/placeholder/300/300' },
    { id: 2, name: 'Smart Watch', price: 249.99, category: 'electronics', image: '/api/placeholder/300/300' },
    { id: 3, name: 'Wireless Earbuds', price: 129.99, category: 'electronics', image: '/api/placeholder/300/300' },
    { id: 4, name: 'Gaming Keyboard', price: 89.99, category: 'electronics', image: '/api/placeholder/300/300' },
    { id: 5, name: 'Cotton T-Shirt', price: 29.99, category: 'clothing', image: '/api/placeholder/300/300' },
    { id: 6, name: 'Denim Jeans', price: 59.99, category: 'clothing', image: '/api/placeholder/300/300' },
    { id: 7, name: 'Running Shoes', price: 89.99, category: 'footwear', image: '/api/placeholder/300/300' },
    { id: 8, name: 'Sports Water Bottle', price: 24.99, category: 'accessories', image: '/api/placeholder/300/300' },
  ];

  const categories = ['all', 'electronics', 'clothing', 'footwear', 'accessories'];

  const filteredProducts = selectedCategory === 'all' 
    ? products 
    : products.filter(product => product.category === selectedCategory);

  const sortedProducts = [...filteredProducts].sort((a, b) => {
    if (sortBy === 'name') return a.name.localeCompare(b.name);
    if (sortBy === 'price') return a.price - b.price;
    return 0;
  });

  return (
    <div className="min-h-screen bg-gray-50 py-12">
      <div className="max-w-7xl mx-auto px-4">
        <motion.div 
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="text-center mb-12"
        >
          <h1 className="text-5xl font-bold mb-6">Our Products</h1>
          <p className="text-2xl text-gray-600">Discover our amazing collection</p>
        </motion.div>

        {/* Filters and Sorting */}
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="flex flex-col md:flex-row justify-between items-center mb-12 p-6 bg-white rounded-xl shadow-sm"
        >
          <div className="flex flex-wrap gap-4 mb-4 md:mb-0">
            {categories.map(category => (
              <button
                key={category}
                onClick={() => setSelectedCategory(category)}
                className={`px-6 py-2 rounded-full capitalize ${
                  selectedCategory === category
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                {category}
              </button>
            ))}
          </div>

          <div className="flex items-center space-x-4">
            <label className="text-gray-700">Sort by:</label>
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            >
              <option value="name">Name</option>
              <option value="price">Price</option>
            </select>
          </div>
        </motion.div>

        {/* Products Grid */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.4 }}
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8"
        >
          {sortedProducts.map((product, index) => (
            <motion.div
              key={product.id}
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              whileHover={{ y: -10 }}
              className="bg-white rounded-xl shadow-md overflow-hidden group"
            >
              <div className="h-64 bg-gray-200 overflow-hidden">
                <div className="w-full h-full bg-gray-300 group-hover:scale-110 transition-transform duration-300"></div>
              </div>
              <div className="p-6">
                <span className="text-sm text-blue-600 font-semibold capitalize mb-2 inline-block">
                  {product.category}
                </span>
                <h3 className="text-xl font-semibold mb-2">{product.name}</h3>
                <p className="text-lg text-blue-600 font-semibold mb-4">${product.price.toFixed(2)}</p>
                <div className="flex space-x-4">
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    className="btn btn-primary flex-1"
                  >
                    Add to Cart
                  </motion.button>
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    className="btn btn-outline"
                  >
                    Details
                  </motion.button>
                </div>
              </div>
            </motion.div>
          ))}
        </motion.div>

        {sortedProducts.length === 0 && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="text-center py-12"
          >
            <p className="text-2xl text-gray-600">No products found in this category.</p>
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default ProductPage;
