import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const Header = ({ user, onLogout, cartCount }) => {
  return (
    <header className="header">
      <div className="container mx-auto px-6 py-6 flex justify-between items-center">
        {/* Logo */}
        <Link to="/" className="text-4xl font-bold text-gradient bg-gradient-to-r from-blue-600 to-purple-600">
          üõçÔ∏è KH STORE
        </Link>

        {/* Navigation */}
        <nav className="hidden md:flex space-x-10">
          <Link to="/" className="text-2xl font-semibold text-gray-800 hover:text-blue-600 transition-colors duration-300">
            Home
          </Link>
          <Link to="/products" className="text-2xl font-semibold text-gray-800 hover:text-blue-600 transition-colors duration-300">
            Products
          </Link>
          <Link to="/cart" className="text-2xl font-semibold text-gray-800 hover:text-blue-600 transition-colors duration-300">
            Cart ({cartCount})
          </Link>
          <Link to="/contact" className="text-2xl font-semibold text-gray-800 hover:text-blue-600 transition-colors duration-300">
            Contact
          </Link>
        </nav>

        {/* User Auth Section */}
        <div className="flex items-center space-x-6">
          {user ? (
            <motion.div 
              className="flex items-center space-x-6"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
            >
              <span className="text-2xl font-semibold text-gray-800">Welcome, {user.name}</span>
              <motion.button 
                onClick={onLogout}
                className="bg-red-600 text-white px-8 py-3 rounded-2xl text-xl font-semibold hover:bg-red-700 transition-colors duration-300 shadow-lg"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                Logout
              </motion.button>
            </motion.div>
          ) : (
            <motion.div 
              className="flex space-x-6"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
            >
              <Link to="/login">
                <motion.button 
                  className="bg-gradient-to-r from-blue-600 to-purple-600 text-white px-8 py-3 rounded-2xl text-xl font-semibold hover:from-blue-700 hover:to-purple-700 transition-all duration-300 shadow-lg"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  Login
                </motion.button>
              </Link>
              <Link to="/register">
                <motion.button 
                  className="border-2 border-blue-600 text-blue-600 px-8 py-3 rounded-2xl text-xl font-semibold hover:bg-blue-600 hover:text-white transition-all duration-300"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  Sign Up
                </motion.button>
              </Link>
            </motion.div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
