import { motion } from 'framer-motion';
import { categories } from '../data/categories';
import { products } from '../data/products';
import CategoryCard from '../components/CategoryCard';
import Product from '../components/Product';

const HomePage = ({ user, cart, addToCart }) => {
  const featuredProducts = products.slice(0, 4);

  return (
    <div className="centered-page bg-home">
      <div className="centered-container">
        {/* Hero Section */}
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          className="text-center mb-20"
        >
          <h1 className="text-7xl font-bold mb-6 text-gradient">
            Welcome to KH Store
          </h1>
          <p className="text-2xl text-gray-600 mb-8">
            Discover amazing products across various categories
          </p>
        </motion.div>

        {/* Categories Section */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.3 }}
          className="mb-20"
        >
          <h2 className="text-5xl font-bold text-center mb-12">Shop by Category</h2>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {categories.map((category, index) => (
              <CategoryCard key={category.id} category={category} />
            ))}
          </div>
        </motion.div>

        {/* Featured Products */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.6 }}
          className="mb-20"
        >
          <h2 className="text-5xl font-bold text-center mb-12">Featured Products</h2>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {featuredProducts.map((product, index) => (
              <motion.div
                key={product.id}
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.1 + 0.8 }}
              >
                <Product product={product} addToCart={addToCart} />
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* Health & Beauty Highlight */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 1.0 }}
          className="text-center"
        >
          <div className="bg-gradient-to-r from-pink-100 to-purple-100 rounded-3xl p-12">
            <h3 className="text-4xl font-bold text-gray-900 mb-4">Health & Beauty</h3>
            <h4 className="text-2xl text-gray-700 mb-6">Skincare & Wellness</h4>
            <p className="text-xl text-gray-600 mb-8">
              Discover our premium collection of health and beauty products for your wellness journey.
            </p>
            <button className="bg-gradient-to-r from-pink-600 to-purple-600 text-white px-8 py-4 rounded-xl text-xl font-semibold hover:from-pink-700 hover:to-purple-700 transition-all duration-300">
              Explore Collection
            </button>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default HomePage;
