import { motion } from 'framer-motion';
import { products } from '../data/products';
import ProductCard from '../components/ProductCard';
import FadeIn from '../components/animations/FadeIn';

const Home = () => {
  const featuredProducts = products.slice(0, 4);

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Hero Section */}
      <section className="mb-16">
        <div className="bg-gradient-to-r from-blue-500 to-purple-600 rounded-2xl p-8 text-white">
          <FadeIn>
            <h1 className="text-4xl md:text-5xl font-bold mb-4">Welcome to Bobo Store</h1>
            <p className="text-xl mb-8">Discover amazing products at unbeatable prices</p>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className="bg-white text-blue-600 px-8 py-3 rounded-full font-semibold text-lg"
            >
              Shop Now
            </motion.button>
          </FadeIn>
        </div>
      </section>

      {/* Featured Products */}
      <section className="mb-16">
        <FadeIn>
          <h2 className="text-3xl font-bold mb-8 text-center">Featured Products</h2>
        </FadeIn>
        
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {featuredProducts.map((product, index) => (
            <motion.div
              key={product.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
            >
              <ProductCard product={product} />
            </motion.div>
          ))}
        </div>
      </section>

      {/* Features Section */}
      <section className="mb-16">
        <FadeIn>
          <h2 className="text-3xl font-bold mb-8 text-center">Why Choose Us</h2>
        </FadeIn>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {[
            { icon: "🚚", title: "Free Shipping", description: "On all orders over $50" },
            { icon: "↩️", title: "Easy Returns", description: "30-day money back guarantee" },
            { icon: "🔒", title: "Secure Payment", description: "All transactions are secure" }
          ].map((feature, index) => (
            <motion.div
              key={index}
              className="bg-white p-6 rounded-xl shadow-md text-center"
              whileHover={{ y: -5 }}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.2 }}
            >
              <div className="text-4xl mb-4">{feature.icon}</div>
              <h3 className="text-xl font-semibold mb-2">{feature.title}</h3>
              <p className="text-gray-600">{feature.description}</p>
            </motion.div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
