import { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Header from './components/Header';
import HomePage from './pages/HomePage';
import ProductPage from './pages/ProductPage';
import CartPage from './pages/CartPage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import ContactPage from './pages/ContactPage';
import './App.css';

// Component to handle background changes
function BackgroundHandler({ children }) {
  const location = useLocation();

  useEffect(() => {
    // Remove all background classes first
    document.body.className = '';

    // Add specific background class based on route
    switch(location.pathname) {
      case '/login':
        document.body.classList.add('bg-login');
        break;
      case '/register':
        document.body.classList.add('bg-register');
        break;
      case '/cart':
        document.body.classList.add('bg-cart');
        break;
      case '/contact':
        document.body.classList.add('bg-contact');
        break;
      case '/products':
        document.body.classList.add('bg-products');
        break;
      default:
        document.body.classList.add('bg-home');
    }
  }, [location.pathname]);

  return children;
}

function App() {
  const [user, setUser] = useState(null);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    const savedCart = localStorage.getItem('cart');

    if (savedUser) setUser(JSON.parse(savedUser));
    if (savedCart) setCart(JSON.parse(savedCart));
  }, []);

  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  const handleLogin = (userData) => {
    const userWithCart = { ...userData, cart };
    setUser(userWithCart);
    localStorage.setItem('user', JSON.stringify(userWithCart));
  };

  const handleSignup = (userData) => {
    const userWithCart = { ...userData, cart };
    setUser(userWithCart);
    localStorage.setItem('user', JSON.stringify(userWithCart));
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  const addToCart = (product, quantity = 1) => {
    const existingItem = cart.find(item => item.id === product.id);

    if (existingItem) {
      setCart(cart.map(item =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + quantity }
          : item
      ));
    } else {
      setCart([...cart, { ...product, quantity }]);
    }
  };

  const removeFromCart = (productId) => {
    setCart(cart.filter(item => item.id !== productId));
  };

  const updateQuantity = (productId, quantity) => {
    if (quantity === 0) {
      removeFromCart(productId);
    } else {
      setCart(cart.map(item =>
        item.id === productId
          ? { ...item, quantity }
          : item
      ));
    }
  };

  const clearCart = () => {
    setCart([]);
  };

  return (
    <Router>
      <BackgroundHandler>
        <div className="app-container">
          <Header user={user} onLogout={handleLogout} cartCount={cart.length} />

          <main className="min-h-screen">
            <Routes>
              <Route path="/" element={<HomePage user={user} cart={cart} addToCart={addToCart} />} />
              <Route path="/products" element={<ProductPage user={user} cart={cart} addToCart={addToCart} />} />
              <Route path="/cart" element={<CartPage user={user} cart={cart} updateQuantity={updateQuantity} removeFromCart={removeFromCart} clearCart={clearCart} />} />
              <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
              <Route path="/register" element={<RegisterPage onSignup={handleSignup} />} />
              <Route path="/contact" element={<ContactPage />} />
              <Route path="*" element={<HomePage user={user} cart={cart} addToCart={addToCart} />} />
            </Routes>
          </main>
        </div>
      </BackgroundHandler>
    </Router>
  );
}

export default App;
